import * as actions from '../actions/types';

const initialState = {
  comments: [],
}

export default function commentReducer(state=initialState, action) {
  switch (action.type) {
    case actions.FETCH_COMMENTS:
      return {
        ...state,
        comments: action.payload,
      }
    default:
      return state;
  }
}
