import { UPDATE_COMMENTS } from "../actions/types";

const initialState = {
  comments: [],
  loading: true,
  error: null,
};

export default function (state = initialState, action) {
  const { type, payload } = action;
  //user loaded and auth error
  switch (type) {
    case UPDATE_COMMENTS:
      let commentState = { ...state };
      if (action.payload?.comments) {
        commentState.comments = [...commentState.comments, action.payload.comment];
      } else {
        commentState.comments = commentState.comments?.filter(
          (comment) => comment.id !== action.payload.comment.id
        );
      }
      return commentState;



    default:
      return state;
  }
}
