import React, { useState } from "react";
import { Button } from "@material-ui/core";

const InformFavButton = (props) => {
  const [favorite, setFavorite] = useState(false);

  if (props.user.favorites.includes(props.blogPost) && !favorite) {
    setFavorite(true);
  }
  if (!props.user.favorites.includes(props.blogPost) && favorite) {
    setFavorite(false);
  }

  const addFavorite = (blogId) => {
    axios
      .post(
        `${process.env.REACT_APP_API}/v1/api/favorites`,
        {
          blog: blogId, // req.body.blog
        },
        {
          headers: {
            "x-auth-token": localStorage.getItem("token"),
          },
        }
      )
      .then((data) => console.log({ data }))
      .catch((e) => console.log(e));
    setFavorite(true);
  };

  function deleteFavorite(blogId) {
    console.log(blogId);
    axios
      .delete(`${process.env.REACT_APP_API}/v1/api/favorites/${blogId}`, {
        headers: {
          "x-auth-token": localStorage.getItem("token"),
        },
      })
      .then((data) => console.log({ data }))
      .catch((e) => console.log(e));
    setFavorite(false);
  }

  return (
    <div>
      {favorite ? (
        <Button size='small' onClick={() => deleteFavorite(blogPost._id)}>
          Delete
        </Button>
      ) : (
        <Button size='small' onClick={() => addFavorite(blogPost._id)}>
          Add to Favorites
        </Button>
      )}
    </div>
  );
};

export default InformFavButton;
