import React, {useState} from "react";
import {Button} from "@material-ui/core";

const InformFavButton = (props) => {
    const [favorite, setFavorite] = useState(false);

    if (props.user.favorites.includes(props.blogPost) && !favorite) {
        setFavorite(true);
    }
    if (!props.user.favorites.includes(props.blogPost) && favorite) {
        setFavorite(false);
    }

    return (
        <div>
                        {favorite ? (
                          <Button
                            size='small'
                            onClick={() => deleteFavorite(blogPost._id)}
                          >
                            Delete
                          </Button>
                        ) : (
                          <Button
                            size='small'
                            onClick={() => addFavorite(blogPost._id)}
                          >
                            Add to Favorites
                          </Button>
                        )}
        </div>

    )
}

export default InformFavButton;
