import React, { useEffect, useState } from "react";
import { TextField, Button } from "@material-ui/core";
import {
  Card,
  CardContent,
  CardActions,
  Typography,
  Box,
} from "@material-ui/core";
import Container from "@material-ui/core/Container";
import axios from "axios";
//redux and responsive buttons if logged in
import { connect, useSelector } from "react-redux";
import PropTypes from "prop-types";
import Title from "./Title";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles({
  container: {
    border: "1px solid black",
  },
  box: {
    width: "100%",
  },
});

const Connect = ({ auth: { isAuthenticated, loading } }) => {
  const classes = useStyles();
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);
  const { user = {} } = useSelector((state) => state.auth);

  console.log({ user });

  const fetchComments = () => {
    const comments = axios
      .get(`${process.env.REACT_APP_API}/v1/api/comments`)
      .then((response) => {
        console.log(response.data);
        setComments(response.data);
      })
      .catch((error) => {
        alert("Error in fetching Comments Info", error);
      });
  };

  useEffect(() => {
    fetchComments();
  }, []);

  const deleteComment = async (id) => {
    console.log(id);
    await axios
      .delete(`${process.env.REACT_APP_API}/v1/api/comments/${id}`)
      .then((res) => console.log("comment deleted"))
      .catch((err) => console.log(err));
    fetchComments();
  };

  const postComment = async (e) => {
    e.preventDefault();
    setComment("");
    const newComment = {
      comment: comment,
    };
    await axios
      .post(`${process.env.REACT_APP_API}/v1/api/comments`, newComment)
      .then((res) => console.log(res.data));

    console.log(comment);
    fetchComments();
  };

  console.log(comment);

  return (
    <Container fixed className={classes.container}>
      <Title title={"chat with your community"} />
      <Box className={classes.box}>
        {isAuthenticated ? (
          <>
            {" "}
            <h1>Add Comment</h1>
            <form onSubmit={(e) => postComment(e)}>
              <TextField
                onChange={(e) => setComment(e.target.value)}
                className=''
                label='Comment'
                variant='outlined'
                color='success'
                fullWidth
                required
                value={comment}
              />

              <Button type='submit' variant='contained' color='success'>
                Post Comment
              </Button>
            </form>
          </>
        ) : (
          <h1>Login to post something</h1>
        )}

        <h1>comments section</h1>

        <form onSubmit={formik.handleSubmit}>
        <div className='form-group'>
          <label htmlFor='name'>Name</label>
          <input
            type='text'
            name='name'
            value={formik.values.name}
            onChange={formik.handleChange}
            className='form-control'
          />
          {formik.errors.name && formik.touched.name ? (
            <div className='collab__errors'>{formik.errors.name}</div>
          ) : null}
        </div>
        <div className='form-group'>
          <label htmlFor='phone'>Phone (optional)</label>
          <input
            type='number'
            name='phoneNum'
            value={formik.values.phoneNum}
            onChange={formik.handleChange}
            className='form-control'
          />

        </div>
        <div className='form-group'>
          <label htmlFor='email'>Email</label>
          <input
            type='text'
            name='email'

            className='form-control'
          />

        </div>
        <div className='form-group'>
          <label htmlFor='message'>Enter your message here</label>
          <textarea
            type='text'
            name='message'
            rows='4'

            className='form-control'
          />

        </div>

      </form>

        {comments?.map((comment) => {
          return (
            <>
              <Card sx={{ minWidth: 275 }} key={comment?._id}>
                <CardContent>
                  <Typography variant='h5' component='div'>
                    {comment?.comment}
                  </Typography>
                  <Typography variant='h5' component='div'>
                    {comment?.user.username}
                  </Typography>
                </CardContent>{" "}
                {isAuthenticated && user?._id === comment?.user._id ? (
                  <CardActions>
                    <Button
                      type='submit'
                      value={comment?._id}
                      onClick={() => deleteComment(comment?._id)}
                    >
                      Delete
                    </Button>
                  </CardActions>
                ) : null}
              </Card>
            </>
          );
        })}
      </Box>
    </Container>
  );
};

Connect.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, null)(Connect);
