import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Button } from "@material-ui/core";
import axios from "axios";

const InformFavButton = (props) => {
  const [favorite, setFavorite] = useState(false);
  const { user = {} } = useSelector((state) => state.auth);

  if (props.user.favorites.includes(props.blogPost) && !favorite) {
    setFavorite(true);
  }
  if (!props.user.favorites.includes(props.blogPost) && favorite) {
    setFavorite(false);
  }

  const addFavorite = (blogId) => {
    axios
      .post(
        `${process.env.REACT_APP_API}/v1/api/favorites`,
        {
          blog: blogId, // req.body.blog
        },
        {
          headers: {
            "x-auth-token": localStorage.getItem("token"),
          },
        }
      )
      .then((data) => console.log({ data }))
      .catch((e) => console.log(e));
    setFavorite(true);
  };

  function deleteFavorite(blogId) {
    console.log(blogId);
    axios
      .delete(`${process.env.REACT_APP_API}/v1/api/favorites/${blogId}`, {
        headers: {
          "x-auth-token": localStorage.getItem("token"),
        },
      })
      .then((data) => console.log({ data }))
      .catch((e) => console.log(e));
    setFavorite(false);
  }

  useEffect(()=>{
    const statusChange = () => {
        if (favorite) {
            setFavorite(false)
        } else (setFavorite(true))
    }
  } )

  return (
    <div>
      {favorite && (
        <Button size='small' onClick={() => deleteFavorite(props.blogPost)}>
          Delete
        </Button>
      )}
      {!favorite && (
        <Button size='small' onClick={() => addFavorite(props.blogPost)}>
          Add to Favorites
        </Button>
      )}
    </div>
  );
};

export default InformFavButton;
