import React, { useEffect, useState } from "react";
import { TextField, Button } from "@material-ui/core";
import { Card, CardContent, CardActions, Typography, Box } from "@material-ui/core";
import Container from "@material-ui/core/Container";
import axios from "axios";
//redux and responsive buttons if logged in
import { connect, useSelector } from "react-redux";
import PropTypes from "prop-types";
import Title from "./Title";
import {makeStyles} from '@material-ui/core/styles';

const useStyles = makeStyles({
  cardContainer: {
    display: "flex",
    flexWrap: "wrap",
    border: "1px solid black",
    justifyContent: "center",
  },
  card: {
    display: "inline-block",
    border: "1px solid #e0e0e0",
    margin: 20,
    flexBasis: "40%",
    justifyContent: "center",
  },
  cardContent: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-evenly",
    alignItems: "flex-end",
  },
});

const Connect = ({ auth: { isAuthenticated, loading } }) => {
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);
  const { user = {} } = useSelector((state) => state.auth);

  console.log({ user });

  const fetchComments = () => {
    const comments = axios
      .get(`${process.env.REACT_APP_API}/v1/api/comments`)
      .then((response) => {
        console.log(response.data);
        setComments(response.data);
      })
      .catch((error) => {
        alert("Error in fetching Comments Info", error);
      });
  };

  useEffect(() => {
    fetchComments();
  }, []);

  const deleteComment = async (id) => {
    console.log(id);
    await axios
      .delete(`${process.env.REACT_APP_API}/v1/api/comments/${id}`)
      .then((res) => console.log("comment deleted"))
      .catch((err) => console.log(err));
    fetchComments();
  };

  const postComment = async (e) => {
    e.preventDefault();
    setComment("");
    const newComment = {
      comment: comment,
    };
    await axios
      .post(`${process.env.REACT_APP_API}/v1/api/comments`, newComment)
      .then((res) => console.log(res.data));

    console.log(comment);
    fetchComments();
  };

  console.log(comment);

  return (
    <Container size='sm'>
      <Title title={"chat with your community"} />
      <Box>
        {isAuthenticated ? (
          <>
            {" "}
            <h1>Add Comment</h1>
            <form onSubmit={(e) => postComment(e)}>
              <TextField
                onChange={(e) => setComment(e.target.value)}
                className=''
                label='Comment'
                variant='outlined'
                color='success'
                fullWidth
                required
                value={comment}
              />

              <Button type='submit' variant='contained' color='success'>
                Post Comment
              </Button>
            </form>
          </>
        ) : (
          <h1>Login to post something</h1>
        )}

        <h1>comments section</h1>

        {comments?.map((comment) => {
          return (
            <>
              <Card sx={{ minWidth: 275 }} key={comment?._id}>
                <CardContent>
                  <Typography variant='h5' component='div'>
                    {comment?.comment}
                  </Typography>
                  <Typography variant='h5' component='div'>
                    {comment?.user.username}
                  </Typography>
                </CardContent>{" "}
                {isAuthenticated && user?._id === comment?.user._id ? (
                  <CardActions>
                    <Button
                      type='submit'
                      value={comment?._id}
                      onClick={() => deleteComment(comment?._id)}
                    >
                      Delete
                    </Button>
                  </CardActions>
                ) : null}
              </Card>
            </>
          );
        })}
      </Box>
    </Container>
  );
};

Connect.propTypes = {
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, null)(Connect);
