import React, { useEffect, useState } from "react";
import { TextField, Button } from "@material-ui/core";
import Container from "@material-ui/core/Container";
import axios from "axios";

const Connect = () => {
  const [comment, setComment] = useState("");
  const [comments, setComments] = useState([]);
  const comment2 = { comment, _id };

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API}/v1/api/comments`)
      .then((res) => setComments(res.data))
      .catch((err) => console.log(err));

    const comment = { comment, _id };
  }, []);

  const onSubmit = async (e) => {
    e.preventDefault();
    setComment("");
    const newComment = {
      comment: comment,
    };
    await axios
      .post(`${process.env.REACT_APP_API}/v1/api/comments`, newComment)
      .then((res) => setComments([...comments, res.data]));

    console.log(comment);
  };

  const deleteComment = async (_id) => {
    console.log(_id);
    await axios
      .delete(`${process.env.REACT_APP_API}/v1/api/comments/${_id}`)
      .then((res) => setComments(res.data))
      .catch((err) => console.log(err));
  };

  return (
    <Container>
      <h1>Connect</h1>
      <h1>Add Comment</h1>
      <form onSubmit={(e) => onSubmit(e)}>
        <TextField
          onChange={(e) => setComment(e.target.value)}
          className=''
          label='Comment'
          variant='outlined'
          color='secondary'
          fullWidth
          required
          value={comment}
        />

        <Button type='submit' variant='contained' color='secondary'>
          Post Comment
        </Button>
      </form>
      <h1>comments section</h1>
      {comments.map((comment) => {
        return (
          <>
            <div key={comment._id}>
              <h3>{comment.comment}</h3>
            </div>
            <Button type='submit' onClick={() => deleteComment(_id)}>
              Delete
            </Button>
          </>
        );
      })}
    </Container>
  );
};

export default Connect;
