import * as actions from '../actions/types';

const initialState = {
  comments: [],
}

export default function commentsReducer (state=initialState, action) {
  switch (action.type) {
    case actions.FETCH_COMMENTS:
      return {
        ...state,
        comments: action.payload,
      }
      case actions.ADD_COMMENT:{

      }
      let newState = {...state};

    default:
      return state;
  }
}
